def setup_stage_training(model, stage, config):
    """
    # Configure which layers are trainable for each stage
    """
    if stage == 1:
        # Stage 1: Only VQ-VAE parameters are trainable
        for param in model.vq_vae.parameters():
            param.requires_grad = True

        print(f"âœ… Stage 1: VQ-VAE parameters enabled for training")
        print(f"ðŸ“Š Trainable parameters: {sum(p.numel() for p in model.vq_vae.parameters() if p.requires_grad):,}")

    elif stage == 2:
        # Stage 2: Freeze VQ-VAE, enable GPT
        for param in model.vq_vae.parameters():
            param.requires_grad = False
        for param in model.gpt.parameters():
            param.requires_grad = True

        print(f"â„ï¸ Stage 2: VQ-VAE frozen")
        print(f"âœ… Stage 2: GPT parameters enabled for training")
        print(f"ðŸ“Š Trainable parameters: {sum(p.numel() for p in model.gpt.parameters() if p.requires_grad):,}")

    elif stage == 3:
        # Stage 3: VQ-VAE frozen, GPT + Critic trainable
        for param in model.vq_vae.parameters():
            param.requires_grad = False
        for param in model.gpt.parameters():
            param.requires_grad = True
        for param in model.critic.parameters():
            param.requires_grad = True

        print(f"â„ï¸ Stage 3: VQ-VAE frozen")
        print(f"âœ… Stage 3: GPT + Critic parameters enabled for training")
        gpt_params = sum(p.numel() for p in model.gpt.parameters() if p.requires_grad)
        critic_params = sum(p.numel() for p in model.critic.parameters() if p.requires_grad)
        print(f"ðŸ“Š Trainable parameters: GPT({gpt_params:,}) + Critic({critic_params:,}) = {gpt_params + critic_params:,}")
